# Initialize an empty list to store the files that meet the criteria
files_meeting_criteria = []
files_not_meeting_criteria = []

# Iterate over all the files in the species folder
for root, dirs, files in os.walk(species_folder):
    for file in files:
        # Check if the file is a JSON file
        if file.endswith('.json'):
            #print(file)
            # Open and parse the JSON file
            with open(os.path.join(root, file), 'r', encoding='utf-8') as f:
                try:
                    data = json.load(f)
                except JSONDecodeError as exception:
                    print(f"Error decoding JSON for file: {file}")
                    print(exception)
                    continue

                # Check if the "implemented" key is present and if its value is not true
                if 'implemented' not in data or data['implemented'] != True:
                    # Check if the Pokemon name is mentioned in the PokemonModelRepository
                    if (data['name']).lower() in pokemon_names_in_repository:
                        # If all conditions are met, add the file to the list of files that meet the criteria
                        files_not_meeting_criteria.append(file)

# Print the files that are not marked as implemented but should be
for file in files_not_meeting_criteria:
    print(file)

print('Done!')
